import React from 'react';

import { Table } from 'antd';
import { AMOUNT, DESIGNATION,QUANTITE, UNITE, UNITE_PRICE} from '../utils/constants';
const Lot =({lignes,label,prixTotalTTC })=>{
    const columns = [
        {
            title: DESIGNATION,
            dataIndex: 'designation',
        },
        {
            title: UNITE,
            dataIndex: 'unite',
        },
        {
            title: QUANTITE,
            dataIndex: 'quantite',
        },
        {
            title: UNITE_PRICE,
            dataIndex: 'unit_price',
        },
        {
          title: AMOUNT,
          dataIndex: 'amount',
        }
      ];

      const data = lignes.map((ligne,i) => {
          return({
            key:i+1,
            designation:ligne.designation,
            unite: ligne.unite, 
            quantite:ligne.quantite, 
            unit_price:ligne.unit_price,
            amount:ligne.amount 
          })});
        console.log('data', data);
    return (
        <Table columns={columns} dataSource={data} size="middle" />
    )
}


export default Lot;
