    //     company: data.company,
    //     title : data.title,
    //     chantierAddress : data.deal.chantierAddress
    // }

    //     ));
    async componentWillMount(){
        this.setState({isLoading: true});
     const reponse = await fetch("https://api.travauxlib.com/api/devis-pro/JKusHl8Ba8MABIjdCtLZOe2lxxnUfX");
     const data = await reponse.json();
     this.setState({ jsonDevis: data, isLoading: false});

    console.log('data', data);



    }

    render(){
        if( this.state.isLoading){
            return <div>sarra</div>
        }
        const {name,email,address,city,postalCode, firstNameRepresentantLegal, lastNameRepresentantLegal}= this.state.company;
        const title= this.state.title;
        // const {chantierAddress: essai, postalCode: chantierPostalCode, city: chantierCity}=this.state.deal.chantierAddress;
        const {address:chantierAddress, postalCode:chantierPostalCode, city:chantierCity}=this.state.chantierAddress;
        console.log('add', this.state.jsonDevis.company.email);

        const representantLegal = 'Legale representant:'+firstNameRepresentantLegal+' '+lastNameRepresentantLegal;

        return(
            <>

            <Row vgutter={12}>
                <Col span={2}>
                    <Logo url={this.state.company.logoUrl}/>
                </Col>
                <Col span={3}>
                    <Address street={address} postalCode={postalCode} city={city}/>
                </Col>
                <Col span={2} offset={14}>

                    <Title level={2} title={name}/>
                </Col>
            </Row>
            <Row gutter={16}>
                <Col span={4}>
                    <Text strong text={representantLegal}/>
                </Col>
            </Row>
            <Row>
                <Col span={3} offset={2}>
                <Address street={chantierAddress} postalCode={chantierPostalCode} city={chantierCity}/>
                </Col>
            </Row>
        <Title title={title}/>
        </>

        );

    }
}


