// ============================================================
// Import packages
import React from 'react';
import { Table } from 'antd';
//import 'antd/dist/antd.css';

import { AMOUNT, DESIGNATION,QUANTITE, UNITE, UNITE_PRICE} from '../utils/constants';
import {Title, Text} from '../components';
const Lot =({lignes,label,totalPriceTTC })=>{
    const columns = [
        {
            title: DESIGNATION,
            dataIndex: 'designation',
        },
        {
            title: UNITE,
            dataIndex: 'unite',
        },
        {
            title: QUANTITE,
            dataIndex: 'quantite',
        },
        {
            title: UNITE_PRICE,
            dataIndex: 'unit_price',
        },
        {
          title: AMOUNT,
          dataIndex: 'amount',
        }
      ];

      const data = lignes.map((ligne,i) => {

        const designation = 
        <div>
          <Text underline>{ligne.designation}</Text>
          <br/>{ligne.description}
        </div>;
          return({
            key:i+1,
            designation:designation,
            unite: ligne.unite, 
            quantite:ligne.quantite, 
            unit_price:ligne.unit_price,
            amount:ligne.amount 
          })});
         data.push({
           key:lignes.length+1,
           designation: 'total',
           amount: totalPriceTTC,


         })
    return (
      <div style={{padding:'70px'}}>
        <Title level={4} title={label}/>
        <Table columns={columns} dataSource={data} size="medium" bordered  summary={pageData=>{ 
          console.log('ss',pageData);
        return(
        <>
          <tr>
              <th>Total</th>
              <td colSpan={2}>
                <Text type="danger">{totalPriceTTC}</Text>
              </td>
            </tr>
        </>
        )}}
          />
      </div>
    )
}

export default Lot;
