import React from 'react';
import { Descriptions } from 'antd';
import { Row, Col,Typography } from 'antd';
import 'antd/dist/antd.css';

import {Address,Logo, Text, Title} from './components';

class Devis extends React.Component{

    constructor(){
        super();
        this.state = {
            jsonDevis:{},
            company: {}
        }
    }

    componentWillMount(){
     fetch("https://api.travauxlib.com/api/devis-pro/JKusHl8Ba8MABIjdCtLZOe2lxxnUfX")
    .then(response=>response.json())
    .then(data=>this.setState({
        company: data.company,
        title : data.title,
        deal : data.deal
    }

        ));


    }

    render(){
        const {name,email,address,city,postalCode, firstNameRepresentantLegal, lastNameRepresentantLegal}= this.state.company;
        const title= this.state.title;
        const {address:chantierAddress } = this.state.deal.address;

        const representantLegal = 'Legale representant:'+firstNameRepresentantLegal+' '+lastNameRepresentantLegal;

        return(
            <>

            <Row >
                <Col span={2}>
                    <Logo url={this.state.company.logoUrl}/>
                </Col>
                <Col span={3}>
                    <Address street={address} postalCode={postalCode} city={city}/>
                </Col>
                <Col span={2} offset={14}>

                    <Title level={2} title={name}/>
                </Col>
            </Row>
            <Row>
                <Col span={4}>
                    <Text strong text={representantLegal}/>
                </Col>
            </Row>
        <Title title={title}/>
        </>

        );

    }
}


