            city,
    }= data.deal.billingAddress;
        this.setState({ 
            isLoading: false,
            company: company,
            billingAddress: billingueAdress,

            devisTitle : data.title,
            chantier : chantier,
            customer: customer,
            date: data.date,
        })

    }
    async componentWillMount(){

        this.setState({isLoading: true});
        const reponse = await fetch("https://api.travauxlib.com/api/devis-pro/JKusHl8Ba8MABIjdCtLZOe2lxxnUfX");
        const data = await reponse.json();
        this.updateDataToState(data);

    }
    render(){
        if( this.state.isLoading){
            return <div>onLoading</div>
        }
        return (<Header company={this.state.company}
        devisTitle={this.state.devisTitle} chantier={this.state.chantier}
        customer={this.state.customer}
        date={this.state.date}/>)
    }
}

export default Devis;
