import React from 'react';
import {Header, Information} from './scene';

class Devis extends React.Component{
    constructor(){
        super();
        this.state = {
            isLoading:true,
        }
        this.updateDataToState = this.updateDataToState.bind(this);
    }
    // async await pareil que fetch.then
    // componentWillMount(){
    //  fetch("https://api.travauxlib.com/api/devis-pro/JKusHl8Ba8MABIjdCtLZOe2lxxnUfX")
    // .then(response=>response.json())
    // .then(data=>this.setState({
    //     jsonDevis: data,
    //     company: data.company,
    //     title : data.title,
    //     chantierAddress : data.deal.chantierAddress
    // }

    //     ));
    updateDataToState(data){
        const {customerName, customerEmail}= data.deal;
        const customer = {
            name:customerName,
            email: customerEmail,
        };
        let {address, postalCode, city}= data.deal.chantierAddress;
        const chantier={
            address,
            postalCode,
            city
        };
        const company ={
          address: data.company.address,
          postalCode: data.company.postalCode,
          city: data.company.city,
          firstNameRepresentantLegal: data.company.firstNameRepresentantLegal,
          lastNameRepresentantLegal: data.company.lastNameRepresentantLegal,
          logoUrl : data.company.logoUrl,
        };
        const billingAddress={
            address: data.deal.billingAddress.address,
            postalCode: data.deal.billingAddress.postalCode,
            city: data.deal.billingAddress.city
        };


    console.log(billingAddress);
        this.setState({ 
            isLoading: false,
            company: company,
            billingAddress: billingAddress,

            devisTitle : data.title,
            chantier : chantier,
            customer: customer,
            date: data.date,
            lots: this
        })

    }
    async componentWillMount(){

        this.setState({isLoading: true});
        const reponse = await fetch("https://api.travauxlib.com/api/devis-pro/JKusHl8Ba8MABIjdCtLZOe2lxxnUfX");
        const data = await reponse.json();
        this.updateDataToState(data);

    }
    render(){
        if( this.state.isLoading){
            return <div>onLoading</div>
        }
        return (
            <>
            <Header 
                company={this.state.company}
                devisTitle={this.state.devisTitle} 
                chantier={this.state.chantier}
                customer={this.state.customer}
                date={this.state.date}
                billingAddress={this.state.billingAddress}/>
                <Information/>
                </>
                )
        }
}

function getLots(lots){
    return lots.map(({designation, description, prixUnitaireHT, quantite,prixHT,tauxTVA,montantTVA,prixTTC})=>({
        designation,
        description,
        prixUnitaireHT,
        quantite,
        unite,
        prixHT,
        tauxTVA,
        montantTVA,
        prixTTC,
    }));
}

export default Devis;
