import React from 'react';
import { Descriptions } from 'antd';
import { Typography } from 'antd';
import 'antd/dist/antd.css';

import {Address,Logo, Text} from './components';
const { Title } = Typography;

class Devis extends React.Component{

    constructor(){
        super();
        this.state = {
            jsonDevis:{},
            company: {}
        }
    }

    componentWillMount(){
     fetch("https://api.travauxlib.com/api/devis-pro/JKusHl8Ba8MABIjdCtLZOe2lxxnUfX")
    .then(response=>response.json())
    .then(data=>this.setState({
        company: data.company,
        title : data.title
    }

        ));


    }

    render(){
        const {name,email,address,city,postalCode, firstNameRepresentantLegal, lastNameRepresentantLegal}= this.state.company;

        const representantLegal = firstNameRepresentantLegal+' '+lastNameRepresentantLegal;

        return(
            <>
        <Logo url={this.state.company.logoUrl}/>
        <CoordinateInformation name={name} email={email} 
        address={address} postalCode={postalCode} city={city}/>
        <Text text={representantLegal}/>
        </>

        );

    }
}


